import React, {Component} from 'react';


class viewbooking extends Component {

    constructor(props){
        super(props)
        this.state ={
            id:null,
            bid:""
        }
    }

     async viewdata(id){
        const response = await fetch('http://localhost:3001/booking/'+id,{
            method: "get",
            mode: "cors",
            headers:{'Content-type': 'application/json'},
        })
        const data= await response.json();
        this.setState({bid:data});

     }   
    handleSubmit=(event)=>{
        event.preventDefault()
        const data=this.state
        const res=data
        console.log(data)

    }
    handleInputChange=(event)=>{
        event.preventDefault()
        console.log(event)
        console.log(event.target.name)
        console.log(event.target.value)
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    
render(){
    const {id,bid}=this.state
    return (
        <div className="container">
            <div className="py-4">
            <form class="form-inline" onSubmit={this.handleSubmit}>
  <div class="form-group mb-2">
    <input type="text" readonly class="form-control-plaintext" 
    id="staticEmail2" value="Enter your Booking ID:"/>
  </div>
  <div class="form-group mx-sm-3 mb-2">
    <label for="inputPassword2" class="sr-only">Booking ID</label>
    <input type="text" class="form-control" 
    name="id"  placeholder="Your Booking ID" onChange={this.handleInputChange}/>
  </div>
  <button type="submit" onClick={()=> this.viewData(id)}
  class="btn btn-success mb-2">View Booking</button>
</form>
<br></br>
<br></br>
<div className="card-columns ">
            {
                data.map(b => (
                <ul key={b.id}>            
                    <div class="card special border-dark mb-1 ">
                
                    <div class="card-body .cardbg text-center">
                      <h4 class="card-title name txt">Booking ID:{b.id}</h4>
                      <p class="card-text txt text-danger">
                      <h5 >Name:</h5> <h5 className="text-info">{b.name}</h5><br/>
                      <h5>Mobile:</h5> <h5 className="text-info">{b.mobile}</h5><br/>
                      <h5>Email ID:</h5> <h5 className="text-info">{b.email}</h5><br/>
                      <h5>No of Persons:</h5> <h5 className="text-info">{b.persons}</h5><br/>
                      </p>
                    </div>
                  </div>
                  <br></br><br></br>
</ul>
                ))
                
            }

        </div>
            </div>
        </div>
    );
}
}
export default viewbooking;
