import React, {useState,useEffect,Component} from 'react';
import './App.css';
import Header from "./components/Header";
import Restaurent from "./components/Restaurent";
import Axios from "axios";
import{BrowserRoutes as Router,Route,Switch} from "react-router-dom";
import About from './Pages/About';
import Contact from './Pages/Contact';
import Navbar from './Layout/Navbar';


function App() {


  const [search,setSearch]= useState("");

  const [restaurent,setRestaurent]=useState([])

  const onInputChange = (e) => {
    setSearch(e.target.value); 
  };


useEffect( () =>{
   getRestaurent();
}, []);


const getRestaurent =async () => {
  var val=search;
  console.log(val);
  const data=await Axios.get('http://localhost:3001/restaurent');
  console.log(data);
  setRestaurent(data.data);
}
  return (
    <div className="App">
      <Navbar />
<Header search={search} onInputChange={onInputChange} />
<div className="bimg">
<div className="container ">
  <div className="py-4">
<Restaurent restaurent={restaurent} />
    </div>
    </div>
    </div>
    <About />
    <Contact />
    </div>

  );
}

export default App;
