import React, {useState,useEffect,Component} from 'react';

const onInputChange = (e) => {
    setSearch(e.target.value); 
  };

  
useEffect( () =>{
   getRestaurent();
}, []);


const getRestaurent =async () => {
  const val=search;
  console.log(val);
  const data=await Axios.get(`http://api.localhost:3001/restaurent/${search}`);
  console.log(data);
  setRestaurent(data.data);
}
const Restaurent = (props) => {
const {restaurent}  =props;
    return (
        <div className="card-columns ">
            {
                restaurent.map(rest => (
                <ul key={rest.id}>            
                    <div class="card special border-dark mb-1 ">
                      
                    <div class="card-header text-light cardhead txt">
                      <h4 ><span class="material-icons">
                            location_on
                            </span> {rest.location}</h4>
                    </div>
                    <div class="card-body .cardbg text-center">
                      <h1 class="card-title name txt">{rest.name}</h1>
                      <p class="card-text txt text-danger">
                      <h5 >Locality</h5> <h5 className="text-info">{rest.area}</h5><br/>
                      <h5>Food Types Available</h5> <h5 className="text-info">{rest.foodtype}</h5><br/>
                      <h5>Average Cost For Two People</h5> <h5 className="text-info">{rest.cost}</h5><br/>
                      <h5>Customers Feedback</h5> <h5 className="text-info">{rest.feed}</h5><br/>
                      <h5>No of Customers voted</h5> <h5 className="text-info">{rest.votes}</h5>
                      </p>
                      <a href="/bookpage"class="btn btn-primary">Book Now</a>
                    </div>
                  </div>
                  <br></br><br></br>
</ul>
                ))
                
            }

        </div>
      
      
    );
};
export default Restaurent;
