import React, {Component} from 'react';


class bookpage extends Component {

    constructor(props){
        super(props)
        this.state ={
            id:"0",
            name: null,
            mobile: null,
            email: null

        }
    }

   async postData(){
        let result = await fetch('http://localhost:3001/booking',{
            method: "POST",
            headers:{'Content-type': 'application/json'},
            body: JSON.stringify({
                "id" : "0",
                "name": "",
                "mobile":"9917004002",
                "email":"name@gmail.com"                
            })
        });
        console.log("Result"+result);
    }
    handleSubmit=(event)=>{
        event.preventDefault()
        const data=this.state
        const res=data
        console.log(data)

    }
    handleInputChange=(event)=>{
        event.preventDefault()
        console.log(event)
        console.log(event.target.name)
        console.log(event.target.value)
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    render() {
        const {name}=this.state
    return (
        <div className="container">
            <div className="py-4">
                <h1>name is: {name}</h1>
            <form onSubmit={this.handleSubmit}>
            <div class="form-group row">
    <label for="colFormLabelLg" class="col-sm-2 col-form-label col-form-label-lg">Name</label>
    <div class="col-sm-10">
      <input  type="text" placeholder="Ex:Jhon" name="name" onChange={this.handleInputChange}/>
    </div>
  </div>
  <div class="form-group row">
    <label for="colFormLabelLg" class="col-sm-2 col-form-label col-form-label-lg">Mobile</label>
    <div class="col-sm-10">
      <input type="text" placeholder="Ex:9917004183" name="mobile"onChange={this.handleInputChange}/>
    </div>
  </div>
  <h1>email is: {name}</h1>

  <div class="form-group row">
    <label for="colFormLabelLg" class="col-sm-2 col-form-label col-form-label-lg">Email</label>
    <div class="col-sm-10">
      <input type="email" placeholder="ex@example.com" name="email"onChange={this.handleInputChange}/>
    </div>
  </div>
  <br></br>
  <button onClick={ ()=> this.postData(name)}
  type="submit" class="btn btn-primary">Submit</button>
</form>

            </div>
        </div>
    );
    }
};

export default bookpage;
